/* eslint-disable prettier/prettier */
import React from 'react';
import { Text, TextInput, View, SafeAreaView, StyleSheet, Button, TouchableOpacity } from 'react-native';
import {IoIosArrowDropleft} from 'react-icons/io'
/*import { LinearGradient } from 'expo';*/
const Login = () => {
  return (
   <SafeAreaView style={styles.body}>
   {/*<LinearGradient start={[0, 0.5]} end={[1, 0.5]} colors={['#EFBB35', '#4AAE9B']} style={{borderRadius:10}}>*/}
    <View style={styles.container}>
      <Text style={styles.login}><IoIosArrowDropleft color="white" style={{fontSize:"40", paddingTop:"10"}}/>Log in</Text>
      <Text style={styles.outsidebox}>Email</Text>
      <TextInput style={styles.box} placeholder="Enter your email" placeholderTextColor="#858585"/>
      <Text style={styles.outsidebox}>Password</Text>
      <TextInput style={styles.box} placeholder="Enter your password" placeholderTextColor="#858585" />
      <TouchableOpacity style={styles.button}>
      <Button title ="Log in" color="#B615DE" borderWidth="0" borderColor="#B615DE"/>
      </TouchableOpacity>
      <Text style={styles.signup}>Dont have an account? <Text style={{color:"#ffff"}}>Sign up</Text></Text>
    </View>
    {/*</LinearGradient>*/}

   </SafeAreaView>
  );
};

const styles = StyleSheet.create({
    container: {
      marginTop: 80,
      marginLeft: 10,
    },
    body: {
     backgroundColor: '#101010',
     flex:1,
    },
    box: {
      marginHorizontal: 16,
      borderWidth: 2,
      padding: 10,
      margin: 10,
      marginBottom:20,
      color: '#858585',
      textAlign: 'left',
      alignItems: 'center',
      justifyContent: 'center',
      borderRadius: 10,
      height:65,
      borderColor:'#212121',
      backgroundColor:'#171717',
    },
    button:{
        marginHorizontal: 17,
        padding: 10,
        marginTop: 40,
        margin: 10,
        color: '#858585',
        textAlign: 'center',
        borderRadius: 10,
        height:55,
        backgroundColor:'#B615DE',
    },
    /*signature:{
    alignContent:'center',
    justifyContent:'center',
    marginTop:100,
    margin:10,
    color:'#858585',
    },
*/
    outsidebox:{
        marginHorizontal:22,
        color:'white',
    },
    signup:{
    alignContent:'center',
    textAlign:'center',
    color: '#858585',
    },
    login:{
     marginBottom:80,
     color:'#ffff',
     alignItems:'center',
     marginLeft:40,
     fontSize:30,
     fontWeight:'600',
    },
});



export default Login;
